var shuzu=new Array();var index=0;$(function(){var obj=$("input[type='text'],select,input[type='radio']");$(obj).each(function(i,ob){shuzu[i]=$(ob).attr("id")});for(var i=0;i<shuzu.length;i++){$("#"+shuzu[i]).keyup(function(event){var e=event||window.event;e.preventDefault();var code=e.keyCode||e.which;if(code==13){var dqvalue=$(this).val();var type=$(this).attr("type");if(dqvalue==""&&type!="radio"){alert("是空");$(this).focus()}else{index++;$("#"+shuzu[index]).focus()}}});$("#"+shuzu[i]).click(function(){var id=$(this).attr("id");fuzhi(id)})}});function fuzhi(id){for(var i=0;i<shuzu.length;i++){if(id==shuzu[i]){index=i}}};